import "./App.css";
import axios from "axios";
import User from "./user.jpg";
import ai from "./ai.png";
// import arrow from './arrow.png'
import { useRef, useState } from "react";

const YOU = "you";
const AI = "ai";
function App() {
  const inputRef = useRef();
  // const submit = false;
  const [isLoading, setIsLoading] = useState(false);

  const [data, setData] = useState([]);

  const handleClick = (event) => {
    event.preventDefault();
    const inputValue = inputRef.current.value;
    setData((prevData) => [...prevData, { from: YOU, value: inputValue }]);
    setIsLoading(true);

    axios
      .post("http://localhost:5000/chat", {
        question: inputValue,
      })
      .then(function (response) {
        setIsLoading(false);
        // console.log(response)
        setData((prevData) => [
          ...prevData,
          { from: AI, value: response.data },
        ]);
      })
      .catch(function (error) {
        console.log(error);
      });

    inputRef.current.value = "";
  };

  const handle = (data) => {
    if (data.from === YOU) {
      return (
        <div className="user">
          <div className="user-info">{data.value}</div>
          <span>
            <img src={User} alt="" />
          </span>
        </div>
      );
    }
    let content = data.value.map(it => {if(it.length>0){return it+<br></br>;}return '';});
    return (
      <div className="ai">
        <span>
          <img src={ai} alt="" />
        </span>
        <div className="ai-info">{content}</div>
      </div>
    );
  };
  return (
    <div className="App">
      <div className="container">{data.map(handle)}
      {isLoading && (
          <div className="ai">
            <span>
              <img src={ai} alt="" />
            </span>
            <div className="ai-info">Typing...</div>
          </div>
      )}
 </div>

      <div className="form-css">
        <div className="form-css">
          <form onSubmit={handleClick}>
            <input
              type="text"
              name="question"
              ref={inputRef}
              autoComplete="off"
            />
            <button type="submit">Send</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
