import "./App.css";
import axios from "axios";
import User from "./user.jpg";
import ai from "./ai.png";
import arrow from './arrow.png'
import { useRef, useState } from "react";

const YOU = "you";
const AI = "ai";
function App() {
  const inputRef = useRef();

  const [data, setData] = useState([]);
  const [isSubmit, setSubmit] = useState(false);

  const handleClick = (event) => {
    event.preventDefault();
    const inputValue = inputRef.current.value;
    setData((prevData) => [...prevData, { from: YOU, value: inputValue }]);

    axios
      .post("http://localhost:5000/chat", {
        question: inputValue,
      })
      .then(function (response) {
        setData((prevData) => [
          ...prevData,
          { from: AI, value: response.data },
        ]);
      })
      .catch(function (error) {
        console.log(error);
      });

    inputRef.current.value = "";
  };

  const handle = (data) => {
    if (data.from === YOU) {
      return (
        <div className="user">
          <div className="user-info">{data.value}</div>
          <span>
            <img src={User} alt="" />
          </span>
        </div>
      );
    }

    return (
      <div className="ai">
        <span>
          <img src={ai} alt="" />
        </span>
        <div className="ai-info">{data.value}</div>
      </div>
    );
  };
  return (
    <div className="App">
      <div className="container">{data.map(handle)}</div>
      <div className="form-css">
        <form onSubmit={handleClick}>
          <input type="text" name="question" ref={inputRef} />
          <img src={arrow} alt="" />
          {isSubmit && <button type="submit">submit</button>}
        </form>
      </div>
    </div>
  );
}

export default App;
