import "./App.css";
import axios from "axios";
import { useRef, useState } from "react";

const YOU = "you";
const AI = "ai";
function App() {
  const inputRef = useRef();
  const initialState = [
    { from: YOU, value: "hello bot" },
    { from: AI, value: "hello user" },
  ];
  const [data, setData] = useState(initialState);

  const handleClick = () => {
    const inputValue = inputRef.current.value;

    axios
      .post("http://localhost:5000/chat", {
        question: inputValue,
      })
      .then(function (response) {
        setData(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

    inputRef.current.value = "";
  };

  const handle = (data) => {
    if(data.from === YOU){
      return (
        <div className="user">
          <div className="user-info">
            <span>YOU</span>
              {data.value}
          </div>
        </div>
      )
    }

    return (
      <div className="ai">
        <div className="ai-info">
          <span>AI</span>
            {data.value}
        </div>
      </div>
    )

  }
  return (
    <div className="App">
      <div className="container">
        {data.map(handle)}
      </div>
      <input type="text" name="question" ref={inputRef} />
      <button onClick={handleClick}>Submit</button>
    </div>
  );
}

export default App;
